import React, { useEffect, useState } from "react";
import "./SelectCharacter.css";
import { ethers } from "ethers";
import {CONTRACT_ADDRESS, transformCharacterData} from "../../constants";
import myEpicGame from "../../utils/MyEpicGame.json";

const SelectCharacter = ({ setCharacterNFT }) => {
  const[characters, setCharacters] =useState([]);
  const[gameContract, setGameContract] = useState(null);

  useEffect(() => {
    const { ethereum } = window;
    if(ethereum){
      const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      const gameContract = new ethers.Contract(CONTRACT_ADDRESS,myEpicGame.abi,signer);

      setGameContract(gameContract);
    }else{
      console.log("Ethereum object not found");
    }     
  },[]);

  useEffect(() =>{
    //キャラデータ取得
    const getCharacters = async () => {
      try{
        console.log("Getting contract characters to mint");
        const charactersTxn = await gameContract.getAllDefaultCharacters();

        console.log("charactersTxn:", charactersTxn);
        //全てのNFTキャラクターのデータを変換
        const characters = charactersTxn.map((CharacterData) => transformCharacterData(CharacterData));
        //ミント可能なすべてのNFTキャラクターの状態を設定
        setCharacters(characters);
      }catch(error){
        console.error("Something went wrong fetching characters: ", error);
      }
    };
    //gameContractの準備が出来たらNFTキャラを読み込む
    if(gameContract){
      getCharacters();
    }
  },[gameContract]);

  //NFTcharaをフロントエンドにレンダリング
  const renderCharacters  = () =>
   characters.map((character, index) => (
      <div className="character-item" key={character.name}>
        <div className="name-container">
          <p>{character.name}</p>
        </div>
        <img src={character.imageURI} alt={character.name}/>
        <button type="button" className="character-mint-button"
        //onClick={mintCharacterNFTAction(index)}
        >{`Mint ${character.name}`}</button>
      </div>
   ));

  return (
    <div className="select-character-container">
      <h2>⏬ 一緒に戦う NFT キャラクターを選択 ⏬</h2>
      {characters.length > 0 && ( 
        <div className="character-grid">{renderCharacters()}</div>
      )}
    </div>
  );
};
export default SelectCharacter;
